          <option value="unavailable">Sold Out!</option>
        </select>
        <textarea
          name="desc"
          placeholder="Description"
          onChange={this.handleChange}
          value={this.props.details.desc}
        />
        <input
          type="text"
          name="image"
          onChange={this.handleChange}
          value={this.props.details.image}
        />
      </form>
    );
  }
}
